//################### Defaults
var ListBlogLink = "";
var ListCount = 5;
var TitleCount = 66;
var ImageSize = 150;
//################### Function Start
function RecentPost(json) {
document.write('<div class="acme-news-ticker"><span style="display: inline-block;background: #000;"><div class="animate-news acme-news-ticker-label">শিরোনাম:-</div></span><div class="acme-news-ticker-box"><ul class="my-news-ticker">');
for (var i = 0; i < ListCount; i++)
{ 
//################### Variables Declared
var listing= ListImage = ListUrl = ListTitle = ListImage = ListContent = ListConten = ListAuthor = ListTag = ListDate = ListUpdate = ListComments = thumbUrl = TotalPosts = sk = AuthorPic= ListMonth = Y = D = M = m = YY = DD = MM = mm = TT =  "";
//################### Category
if (json.feed.entry[i].category != null)
{
for (var k = 0; k < json.feed.entry[i].category.length; k++) {
ListTag += "<a href='"+ListBlogLink+"/search/label/"+json.feed.entry[i].category[k].term+"'>"+json.feed.entry[i].category[k].term+"</a>";
if(k < json.feed.entry[i].category.length-1)
{ ListTag += " ";}
}
}
//################### URL
for (var j = 0; j < json.feed.entry[i].link.length; j++) {
      if (json.feed.entry[i].link[j].rel == 'alternate') {
        break;
      }
    }
ListUrl= "'" + json.feed.entry[i].link[j].href + "'";
//################### Info
TotalPosts = json.feed.openSearch$totalResults.$t;
if (json.feed.entry[i].title!= null)
{
ListTitle= json.feed.entry[i].title.$t.substr(0, TitleCount);
}
if (json.feed.entry[i].thr$total)
{
ListComments= ""+json.feed.entry[i].thr$total.$t+"";
}
ListAuthor= json.feed.entry[i].author[0].name.$t.split(" ");
ListAuthor=ListAuthor.slice(0, 1).join(" ");
AuthorPic = json.feed.entry[i].author[0].gd$image.src;
//################### Date Format
ListMonth= ["জানুয়ারি", "ফেব্রুয়ারি", "মার্চ", "এপ্রিল", "মে", "জুন", "জুলাই", "আগস্ট", "সেপ্টেম্বর", "অক্টোবর", "নভেম্বর", "ডিসেম্বর"];
ListDate= json.feed.entry[i].published.$t.substring(0,10);

						 Y = ListDate.substring(0, 4);
                         m = ListDate.substring(5, 7);
                         D = ListDate.substring(8, 10);
                         M = ListMonth[parseInt(m - 1)];                       
ListUpdate= json.feed.entry[i].updated.$t.substring(0, 16);
                         YY = ListUpdate.substring(0, 4);
                        mm = ListUpdate.substring(5, 7);
                         DD = ListUpdate.substring(8, 10);
                         TT = ListUpdate.substring(11, 16);
                         MM = ListMonth[parseInt(mm - 1)];   
//################### Thumbnail Check
// YouTube scan
if (json.feed.entry[i].content.$t.match(/youtube\.com.*(\?v=|\/embed\/)(.{11})/) != null)
{
    var youtube_id = json.feed.entry[i].content.$t.match(/youtube\.com.*(\?v=|\/embed\/)(.{11})/).pop();

    if (youtube_id.length == 11) {
        var ListImage = "'//img.youtube.com/vi/"+youtube_id+"/0.jpg'";
        }
}
else if (json.feed.entry[i].media$thumbnail)
{
thumbUrl = json.feed.entry[i].media$thumbnail.url;
sk= thumbUrl.replace("/s72-c/","/s"+ImageSize+"/");
ListImage= "'" + sk.replace("?imgmax=800","") + "'";
}
else if (json.feed.entry[i].content.$t.match(/src=(.+?[\.jpg|\.gif|\.png]")/) != null)
{
// Support For 3rd Party Images
ListImage =  json.feed.entry[i].content.$t.match(/src=(.+?[\.jpg|\.gif|\.png]")/)[1];
}
else
{
ListImage= "'https://blogger.googleusercontent.com/img/b/R29vZ2xl/AVvXsEhGBaoO_i2u7kTRZvG6GBbHHO97n5RtxCjYQeBEoyXWIVKquEE5xXh4kgUGZnVBKy5yfkOYCTitIMnhUgO8zZHT7Oa0SN5EEVh5JgdI3KUj66cqgZ7EVuzPXOV_pewp5n0Ydrog0K1Rdfs/s200/Icon.png'";
}
//###################  Printing List
var listing = "<li><a href="+ListUrl+">"+ListTitle+" , সংবাদদাতা - "+ListAuthor+" , "+ListComments+" টি মন্তব্য রয়েছে এবং খবর টি প্রকাশিত হয়েছে - "+ M +" "+ D +", "+ Y +" ইং</a></li>";

document.write(listing);
} }

document.write("</ul></div><div class='acme-news-ticker-controls acme-news-ticker-horizontal-controls'><button class='acme-news-ticker-arrow acme-news-ticker-prev'></button><button class='acme-news-ticker-pause'></button><button class='acme-news-ticker-arrow acme-news-ticker-next'></button></div></div>");

<!-- #### Invoking the Callback Function #### -->
if (ListLabel == "Recent") {
	document.write("<script src='/feeds/posts/default?alt=json-in-script&callback=RecentPost'></"+"script>");
}
else           
{
	document.write("<script src='/feeds/posts/default/-/"+ListLabel+"?alt=json-in-script&callback=RecentPost'></"+"script>");
}
