//----------------------------Defaults           
var ListBlogLink = "";           
var ListCount = 4;           
var TitleCount = 50;
var ChrCount = 100;           
var ImageSize = 800; 


//----------------------------Function Start          
function mbtlist(json) {           
document.write('<ul class="content-items-x">');           
for (var i = 0; i < ListCount; i++)           
{


//-----------------------------Variables Declared          
var listing= ListImage = ListUrl = ListTitle = ListImage = ListContent = ListConten = ListProfile = ListAuthor = ListTag = ListDate = ListUpdate = ListComments = thumbUrl = TotalPosts = sk = AuthorPic= ListMonth = Y = D = M = m = YY = DD = MM = mm = TT =  "";           
//----------------------------- Title URL           
for (var j = 0; j < json.feed.entry[i].link.length; j++) {           
if (json.feed.entry[i].link[j].rel == 'alternate') {           
break;           
}           
}           
ListUrl= "'" + json.feed.entry[i].link[j].href + "'";           
//----------------------------------- Title Stirng           
if (json.feed.entry[i].title!= null)           
{           
ListTitle= json.feed.entry[i].title.$t.substr(0, TitleCount);           
}


if (json.feed.entry[i].thr$total.$t == 0) {
    ListComments = "<a href='"+json.feed.entry[i].link[j].href+"#comment-form'>কোন মন্তব্য নেই</a>";
  } else 
{           
ListComments= "<a href='"+json.feed.entry[i].link[j].href+"#comment-form'>"+json.feed.entry[i].thr$total.$t+" টি মন্তব্য</a>";           
}
ListProfile= "'" + json.feed.entry[i].author[0].uri.$t + "'";
ListAuthor= json.feed.entry[i].author[0].name.$t;
AuthorPic = json.feed.entry[i].author[0].gd$image.src;


//################### Content Check           
ListConten = json.feed.entry[i].content.$t;           
ListContent= ListConten.replace(/(<([^>]+)>)/ig,"").substring(0, ChrCount);


//################### Date Format


ListMonth= ["January", "February", "March", "April", "May", "Jun", "July", "August", "September", "October", "Novembor", "Decembor"];


ListDate= json.feed.entry[i].published.$t.substring(0,10);


                         Y = ListDate.substring(0, 4);          
                        m = ListDate.substring(5, 7);           
                         D = ListDate.substring(8, 10);           
                         M = ListMonth[parseInt(m - 1)];  


//################### Thumbnail Check          
if (json.feed.entry[i].media$thumbnail)           
{           
thumbUrl = json.feed.entry[i].media$thumbnail.url;           
sk= thumbUrl.replace("/s72-c/","/s"+ImageSize+"/");           
ListImage= "'" + sk.replace("?imgmax=800","") + "'";           
}


// YouTube scan          
else if (json.feed.entry[i].content.$t.match(/youtube\.com.*(\?v=|\/embed\/)(.{11})/) != null)           
{           
    var youtube_id = json.feed.entry[i].content.$t.match(/youtube\.com.*(\?v=|\/embed\/)(.{11})/).pop();           

    if (youtube_id.length == 11) {           
        var ListImage = "'//img.youtube.com/vi/"+youtube_id+"/0.jpg'";           
        }           
}



else if (json.feed.entry[i].content.$t.match(/src=(.+?[\.jpg|\.gif|\.png]")/) != null)           
{           
// Support For 3rd Party Images           
ListImage =  json.feed.entry[i].content.$t.match(/src=(.+?[\.jpg|\.gif|\.png]")/)[1];           
}           
else           
{           
ListImage= "'https://blogger.googleusercontent.com/img/a/AVvXsEiq3HyqTZTY_h5o2LR7_BMFxOcmlVGr_qca9gRnqCor71GLVdubraWidu-VEMpneyKC3axlpSVyvCJMJuKdd6DJziMhstWX4ToFMR2hVpceGNaVJC1vYGO72ReDcihpV0sKGupYtESzUixljAdGGJhvT-mQ5-AHLUfoKDf7lPS1dsc3g9yAYf_uhCEEX5-R=s250'";           
} 


//----------------------------------- Printing List          
var listing = "<li class='col-md-3'><div class='content-item812'><a href="+ListUrl+"><img src="+ListImage+" alt='image'></a><div class='wrapper789'><a href="+ListUrl+"><h2>"+ListTitle+"</h2></a><a href="+ListUrl+"><p>"+ListContent+"</p></a><a href='"+ListBlogLink +"/search/label/"+ListLabel+"'><h6><i aria-hidden='true' class='fa fa fa-folder-open'></i> "+ListLabel+"</h6></a></div></div></li>";           
document.write(listing);
}}

