body.script:
var d = new Date();
var days = ["রবিবার","সোমবার","মজ্ঞলবার","বুধবার","বৃহস্পতিবার","শুক্রবার","শনিবার"];
document.getElementById("smsudipbd-days").innerHTML = days[d.getDay()];

var d = new Date();
document.getElementById("smsudipbd-date").innerHTML = d.getDate();

var months = ["জানুয়ারী", "ফ্রেব্রুয়ারী", "মার্চ", "এপ্রিল", "মে", "জুন", "জুলাই", "আগস্ট","সেপ্টেম্বর","অক্টোবর","নভেম্বর", "ডিসেম্বর"];
document.getElementById("smsudipbd-month").innerHTML = months[d.getMonth()];

var d = new Date();
document.getElementById("smsudipbd-years").innerHTML = d.getFullYear();

const correction = {
  "AM": "পূর্বাহ্ন",
  "PM": "অপরাহ্ণ"
};
const numbersInBangla = ['০','১','২','৩','৪','৫','৬','৭','৮','৯'];
var myVar = setInterval(function () { myTimer() }, 1000);
function myTimer() {
var d = new Date();
var t = d.toLocaleTimeString().toString().replace(/[0-9]/g, digit => numbersInBangla[digit]);
var final = t.toString().replace(/AM|PM/g, matched => correction[matched]);
document.getElementById("time-now").innerHTML = final;
}

