var msg=document.getElementsByName("smsudipbd-bn");
for(var i=0;i<msg.length;i++){ var txt=document.getElementsByName("smsudipbd-bn")[i].innerHTML;
txt=txt.replace(/0/g, "০");
txt=txt.replace(/1/g, "১");
txt=txt.replace(/2/g, "২");
txt=txt.replace(/3/g, "৩");
txt=txt.replace(/4/g, "৪");
txt=txt.replace(/5/g, "৫");
txt=txt.replace(/6/g, "৬");
txt=txt.replace(/7/g, "৭");
txt=txt.replace(/8/g, "৮");
txt=txt.replace(/9/g, "৯");
txt=txt.replace(/January/g, "জানুয়ারি");
txt=txt.replace(/February/g, "ফেব্রুয়ারি");
txt=txt.replace(/March/g, "মার্চ");
txt=txt.replace(/April/g, "এপ্রিল");
txt=txt.replace(/May/g, "মে");
txt=txt.replace(/June/g, "জুন");
txt=txt.replace(/July/g, "জুলাই");
txt=txt.replace(/August/g, "আগস্ট");
txt=txt.replace(/September/g, "সেপ্টেম্বর");
txt=txt.replace(/October/g, "অক্টোবর");
txt=txt.replace(/November/g, "নভেম্বর ");
txt=txt.replace(/December/g, "ডিসেম্বর");
document.getElementsByName("smsudipbd-bn")[i].innerHTML=txt;
}//

function mySearch() {
   var a = document.getElementById("cd-overlay");
   a.classList.toggle("is-visible");
   var b = document.getElementById("cd-search");
   b.classList.toggle("is-visible");
   var c = document.getElementById("cd-search-trigger");
   c.classList.toggle("search-is-visible");
}

function myMenu() {
   var a = document.getElementById("mymenu");
   a.classList.toggle("selected");
   var b = document.getElementById("cd-secondary-nav");
   b.classList.toggle("is-hidden");
}

function myNavMenu() {
   var a = document.getElementById("cd-nav-trigger");
   a.classList.toggle("nav-is-visible");
   var b = document.getElementById("cd-main-header");
   b.classList.toggle("nav-is-visible");
   var c = document.getElementById("cd-overlay");
   c.classList.toggle("nav-is-visible");
   var d = document.getElementById("cd-main-content");
   d.classList.toggle("nav-is-visible");
   var e = document.getElementById("cd-primary-nav");
   e.classList.toggle("nav-is-visible");
}

